body.script:
// Example: simple game loop, replace this with your game logic
const canvas = document.getElementById('gameCanvas');
const ctx = canvas.getContext('2d');

function resize() {
  canvas.width = window.innerWidth * 0.85; // same size as iframe
  canvas.height = window.innerHeight * 0.8;
}
window.addEventListener('resize', resize);
resize();

// Simple demo loop
let x = 50, y = 50, dx = 2, dy = 2;
function gameLoop() {
  ctx.fillStyle = '#000';
  ctx.fillRect(0, 0, canvas.width, canvas.height);

  // Moving square
  ctx.fillStyle = '#ff4e50';
  ctx.fillRect(x, y, 50, 50);

  x += dx;
  y += dy;

  if (x + 50 > canvas.width || x < 0) dx = -dx;
  if (y + 50 > canvas.height || y < 0) dy = -dy;

  requestAnimationFrame(gameLoop);
}

gameLoop();

